sql.where:
1=1
 and c.category_id=#{categoryId} 
 and c.category_name=#{categoryName} 
 and c.category_description=#{categoryDescription} 
 and c.category_levelCode=#{categoryLevelcode} 
 and c.category_href=#{categoryHref} 
 and c.category_image=#{categoryImage} 
 and c.category_priority=#{categoryPriority} 
 and c.category_status=#{categoryStatus} 
 and c.category_enable=#{categoryEnable} 
 and c.category_delete=#{categoryDelete} 
 and c.create_time=#{createTime} 
 and c.create_user=#{createUser} 
 and c.modify_time=#{modifyTime} 
 and c.modify_user=#{modifyUser} 
 and c.category_extend=#{categoryExtend}

mapper.update:
update cms_category c set c.modify_time = now(),c.modify_user=#{modifyUser}
	  c.category_name=#{categoryName} 
	  c.category_description=#{categoryDescription} 
	  c.category_levelCode=#{categoryLevelcode} 
	  c.category_href=#{categoryHref} 
	  c.category_image=#{categoryImage} 
	 c.category_priority=#{categoryPriority} 
	 c.category_status=#{categoryStatus} 
	 c.category_enable=#{categoryEnable} 
	 c.category_delete=#{categoryDelete} 
	  c.category_extend=#{categoryExtend} 
    where category_id=#{categoryId}
